import React, { Component } from "react";
import Slide from "react-reveal";

class Resume extends Component {
  getRandomColor() {
    let letters = "0123456789ABCDEF";
    let color = "#";
    for (let i = 0; i < 6; i++) {
      color += letters[Math.floor(Math.random() * 16)];
    }
    return color;
  }

  render() {
    if (!this.props.data) return null;

    const mensagemHabilidade = this.props.data.mensagemHabilidade;



    const Habilidades = this.props.data.Habilidades.map((Habilidades) => {
      const backgroundColor = this.getRandomColor();
      const className = "bar-expand " + Habilidades.name.toLowerCase();
      const width = Habilidades.level;

      return (
        <li key={Habilidades.name}>
          <span style={{ width, backgroundColor }} className={className}></span>
          <em>{Habilidades.name}</em>
        </li>
      );
    });

    return (
      <section id="resume">

        <Slide left duration={1300}>
          <div className="row skill">
            <div className="three columns header-col">
              <h1>
                <span>Habilidades</span>
              </h1>
            </div>

            <div className="nine columns main-col">
              <p>{mensagemHabilidade}</p>

              <div className="bars">
                <ul className="Habilidades">{Habilidades}</ul>
              </div>
            </div>
          </div>
        </Slide>
      </section>
    );
  }
}

export default Resume;
